// router.js

import { createRouter, createWebHistory } from 'vue-router'; // createRouter ve createWebHistory ekledik
import App from './App.vue';

const routes = [
  {
    path: '/',
    name: 'App',
    component: App
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes
});

export { router }; // router'ı export ettik
